function animateCounter(id, target, duration) {
    let element = document.getElementById(id);
    let start = 0;
    let increment = target / (duration * 100);
    let current = 0;

    function updateCounter() {
        current += increment;
        if (current >= target) {
            current = target;
            clearInterval(timer);
        }
        element.innerText = Math.floor(current);
    }

    let timer = setInterval(updateCounter, 10);
    element.style.color = 'red'; // Set the initial text color to red
}

