// Add smooth scrolling to the toolbar links
document.querySelectorAll('.toolbar a').forEach(link => {
    link.addEventListener('click', (event) => {
        event.preventDefault();

        const targetClass = link.getAttribute('href').substring(1); // Get the target class
        const targetElements = document.querySelectorAll('.' + targetClass);

        if (targetClass === 'top') {
            // Scroll to the top of the page
            window.scrollTo({ top: 0, behavior: 'smooth' });
        } else if (targetElements.length > 0) {
            // Scroll to the first matching element (you can refine this if there are multiple)
            targetElements[0].scrollIntoView({ behavior: 'smooth' });
        }
    });
});

let prevScrollPos = window.pageYOffset;

window.addEventListener('scroll', () => {
    let currentScrollPos = window.pageYOffset;

    if (prevScrollPos > currentScrollPos) {
        // Scrolling up, show the top section
        document.querySelector('.top-section').classList.remove('hidden');
    } else {
        // Scrolling down, hide the top section
        document.querySelector('.top-section').classList.add('hidden');
    }

    prevScrollPos = currentScrollPos;
});




