function typeText() {
    let charIndex = 0;
    const typingSpeed = 0; // Adjust typing speed (lower value for faster, higher for slower)

    function typeCharacter() {
        if (charIndex < textsToType[currentTextIndex].length) {
            textElements[currentTextIndex].textContent += textsToType[currentTextIndex].charAt(charIndex);
            charIndex++;
            setTimeout(typeCharacter, typingSpeed);
        } else {
            currentTextIndex++;
            if (currentTextIndex < textElements.length) {
                setTimeout(() => {
                    textElements[currentTextIndex].textContent = ''; // Clear text for the next element
                    typeText();
                }, 1000);
            } else {
                // All texts have been typed
                fadeInImages(); // Start fading in the images
            }
        }
    }

    typeCharacter();
}

function fadeInImages() {
    let index = 0;
    const fadeInSpeed = 500; // Adjust fade-in speed (lower value for slower, higher for faster)

    function fadeInImage() {
        if (index < images.length) {
            let img = images[index];
            img.style.transition = `opacity ${fadeInSpeed}ms ease-in-out`;
            img.style.opacity = 1;
            index++;
            setTimeout(fadeInImage, fadeInSpeed);
        }
    }

    fadeInImage();
}

// Function to trigger the typing animation when the element is in view
function checkIfInView() {
    const rect = textElements[0].getBoundingClientRect();
    const windowHeight = window.innerHeight || document.documentElement.clientHeight;

    if (rect.top >= 0 && rect.bottom <= windowHeight) {
        typeText(currentTextIndex);
        window.removeEventListener('scroll', checkIfInView);
    }
}

// Add a scroll event listener to trigger the animation when the element is in view
window.addEventListener('scroll', checkIfInView);

