images.forEach(image => {
    image.addEventListener('mouseover', () => {
        images.forEach(otherImage => {
            if (otherImage !== image) {
                otherImage.style.filter = 'brightness(0.8)';
            }
        });
    });

    image.addEventListener('mouseout', () => {
        images.forEach(otherImage => {
            otherImage.style.filter = 'brightness(1)';
        });
    });
});

